import React, { useEffect, useState } from 'react';
import './AdminDisplay.css';

export default function AdminDisplay() {
  const [foodItems, setFoodItems] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('https://meal-rescue-india-default-rtdb.firebaseio.com/Fooddata.json');
        const data = await response.json();

        // Ensure data is an array before setting the state
        if (Array.isArray(data)) {
          setFoodItems(data);
        } else {
          console.error('Invalid data format. Expected an array.');
        }
      } catch (error) {
        console.error('Error fetching data', error);
      }
    }

    fetchData();
  }, []);

  const declinesetter = (itemIndex) => {
    const updatedItems = [...foodItems];
    updatedItems.splice(itemIndex, 1);
    setFoodItems(updatedItems);
  };

  return (
    <div>
      {foodItems.slice(0, 7).map((item, index) => (
        <div className='output' key={index}>
          <div className='list'>
            <h1>{item.name}</h1>
          </div>
          <div className='list'>
            <h1>{item.mfg_exp}</h1>
          </div>
          <div className='list'>
            <h1>{item.exp_date}</h1>
          </div>
          <div className='list'>
            <h1>{item.location}</h1>
          </div>
          <button
            onClick={() => declinesetter(index)}
            className='button-68'
            role='button'
            style={{ backgroundColor: '#C20114' }}
          >
            Decline
          </button>
          <button
            className='button-68'
            role='button'
            style={{ backgroundColor: '#4DA167' }}
          >
            Accept
          </button>
        </div>
      ))}
    </div>
  );
}
