import React, { useEffect, useState } from 'react';
import './AdminDisplay.css';

export default function AdminDisplay() {
  const [foodItems, setFoodItems] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('https://meal-rescue-india-default-rtdb.firebaseio.com/Fooddata.json');
        const data = await response.json();

        // Convert data to an array using Object.values
        const dataArray = Array.isArray(data) ? data : Object.values(data);

        setFoodItems(dataArray);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    }

    fetchData();
  }, []);

  const deleteItem = async (itemIndex) => {
    try {
      const itemIdToDelete = foodItems[itemIndex].id; // Assuming there is an "id" field in your data
      const response = await fetch(`https://meal-rescue-india-default-rtdb.firebaseio.com/Fooddata/${itemIdToDelete}.json`, {
        method: 'DELETE',
      });

      if (response.ok) {
        console.log('Item deleted successfully');
        const updatedItems = [...foodItems];
        updatedItems.splice(itemIndex, 1); // splices , removes the item from the rendering list.
        setFoodItems(updatedItems);
      } else {
        console.error('Failed to delete item');
      }
    } catch (error) {
      console.error('Error deleting item', error);
    }
  };
  return (
    <div>
      {foodItems.map((item, index) => (
        <div className='output' key={index}>
          <div className='list'>
            <h1>{item.food}</h1>
          </div>
          <div className='list'>
            <h1>{item.manufacturingDate}</h1>
          </div>
          <div className='list'>
            <h1>{item.expiry}</h1>
          </div>
          <div className='list'>
            <h1>{item.location}</h1>
          </div>
          <button
            onClick={() => deleteItem(index)}
            className='button-68'
            role='button'
            style={{ backgroundColor: '#C20114' }}
          >
            Decline
          </button>
          <button
            className='button-68'
            role='button'
            style={{ backgroundColor: '#4DA167' }}
          >
            Accept
          </button>
        </div>
      ))}
    </div>
  );
}
