import React from 'react'
import './AdminDisplay.css'
import { useState } from 'react'
export default function AdminDisplay() {
  const foodItems = [
  
    { name: 'Food name', mfg_exp: '12/2023', location: 'indore', exp_date:'10/2024' },
    { name: 'Food name', mfg_exp: '01/2024', location: 'Betul' , exp_date:'10/2024'},
    { name: 'Food name', mfg_exp: '06/2024', location: 'Indore', exp_date:'10/2024' },
    { name: 'Item 4', mfg_exp: '07/2024', location: 'Pantry 4' , exp_date:'10/2024'},
    { name: 'Item 5', mfg_exp: '12/2024', location: 'Pantry 5' , exp_date:'10/2024'},
    { name: 'Item 6', mfg_exp: '03/2024', location: 'Pantry 6' , exp_date:'10/2024'},
    { name: 'Item 7', mfg_exp: '01/2024', location: 'Pantry 7' , exp_date:'10/2024'},
    
  ];
const[decline,setdecline] = useState(foodItems);
const declinesetter = (itemIndex)=>{
  const updatedItems = [...decline];
    updatedItems.splice(itemIndex, 1);
    setdecline(updatedItems);
}

  return (
    <div>
   
      {decline.slice(0,7).map((foodItems,index)=>(<div className='output' key={index}>
        <div className='list'><h1>{foodItems.name}</h1></div>
        <div className='list'><h1>{foodItems.mfg_exp}</h1></div>
        <div className='list'><h1>{foodItems.exp_date}</h1></div>
        <div className='list'><h1>{foodItems.location}</h1></div>
        <button onClick={() =>declinesetter(index)} className="button-68" role="button" style={{ backgroundColor: '#C20114' }}>Decline</button>
        <button  className="button-68" role="button" style={{ backgroundColor: '#4DA167' }}>Accept</button>
      </div>))}
    </div>
  )
}
